    <div class="message" :class="messageColorClass">{{ message }}</div>
 
    <p>{{ text }}</p>
  </div>
</template>

<script>
export default {
  name: 'ChildComponent',
  props: {
    message: {
      type: String,
      required: true
    },
    level: {
      type: String,
      required: true
    },
    text: {
      type: String,
      required: true
    }
  },
  computed: {
    messageColorClass() {
      switch(this.level) {
        case '1': return 'level-1';
        case '2': return 'level-2';
        case '3': return 'level-3';
        default: return '';
      }
    }
  },
  methods: {
      getLevelColor(level) {
        const colorMap = {
          '1': 'green',
          '2': 'rgba(255, 208, 44)',
          '3': 'red',
        }
        return colorMap[level] || '#ccc'
      }
    }
};
</script>

<style scoped>
.child-item {
  position: relative;
  width: 250px;
  height: 200px;
  background: white;
  border-radius: 12px;
  padding: 16px;
}

.head {
    position: absolute;
    font-weight: 700;
    display: flex;
    align-items: center;
    gap: 6px; /* 盒子间距 */
  }
  
  .level-box {
    width: 12px;
    height: 12px;
    border-radius: 2px;
    display: inline-block;
  }

.message {
  font-size: 20px;
  font-weight: 700;
}

/* 颜色等级 */
.level-1 {
  color: green;
}
.level-2 {
  color: rgba(255, 208, 44);
}
.level-3 {
  color: red;
}
p{
  font-size: 14px;
  }
.svg{
  margin-top: 30px;
  margin-bottom: 5px;
}
</style>
